mapper.select:
SELECT h.IV_HISTORY_ID AS ivHistoryId, h.LOT_NO AS lotNo, h.ITEM_NAME AS itemName, h.PREV_LOCATION_ID AS prevLocationId, NVL(pl.ZONE, '') || '-' || NVL(pl.RACK, '') || '-' || NVL(pl.RACK_ROW, '') || '-' || NVL(pl.RACK_COL, '') AS prevLocationName, h.CURRENT_LOCATION_ID AS currentLocationId, NVL(cl.ZONE, '') || '-' || NVL(cl.RACK, '') || '-' || NVL(cl.RACK_ROW, '') || '-' || NVL(cl.RACK_COL, '') AS currentLocationName, h.EMP_ID AS empId, e.EMP_NAME AS empName, h.WORK_TYPE AS workType, h.MOVE_AMOUNT AS moveAmount, h.PREV_AMOUNT AS prevAmount, h.CURRENT_AMOUNT AS currentAmount, h.REASON AS reason, h.CREATED_DATE AS createdDate FROM INVENTORY_HISTORY h LEFT JOIN WAREHOUSE_LOCATION pl ON h.PREV_LOCATION_ID = pl.LOCATION_ID LEFT JOIN WAREHOUSE_LOCATION cl ON h.CURRENT_LOCATION_ID = cl.LOCATION_ID LEFT JOIN EMP e ON h.emp_Id = e.emp_Id WHERE 1 = 1
AND h.CREATED_DATE >= #{startDateTime}
AND h.CREATED_DATE <= #{endDateTime}
AND h.WORK_TYPE = #{workType}
AND (
LOWER(h.LOT_NO) LIKE #{searchKeyword} OR LOWER(h.ITEM_NAME) LIKE #{searchKeyword} OR LOWER( NVL(pl.ZONE, '') || '-' || NVL(pl.RACK, '') || '-' || NVL(pl.RACK_ROW, '') || '-' || NVL(pl.RACK_COL, '') ) LIKE #{searchKeyword} OR LOWER( NVL(cl.ZONE, '') || '-' || NVL(cl.RACK, '') || '-' || NVL(cl.RACK_ROW, '') || '-' || NVL(cl.RACK_COL, '') ) LIKE #{searchKeyword} OR LOWER(h.EMP_ID) LIKE #{searchKeyword} OR LOWER(e.EMP_NAME) LIKE #{searchKeyword} OR LOWER(h.REASON) LIKE #{searchKeyword} )
ORDER BY h.CREATED_DATE DESC
