mapper.select:
SELECT o.OUTBOUND_ID , e1.EMP_NAME AS CREATEDUSER , e2.EMP_NAME AS PROCESSEMPNAME , o.WORK_ORDER_ID , o.SHIPMENT_ID , o.STATUS , o.EXPECT_OUTBOUND_DATE , o.OUTBOUND_DATE , oi.OUTBOUND_ITEM_ID , oi.OUTBOUND_ID , oi.LOT_NO , oi.OUTBOUND_AMOUNT , CASE WHEN o.WORK_ORDER_ID IS NOT NULL THEN mat.MAT_ID END AS MAT_ID , CASE WHEN o.WORK_ORDER_ID IS NOT NULL THEN mat.MAT_NAME END AS MAT_NAME , CASE WHEN o.SHIPMENT_ID IS NOT NULL THEN prd.PRD_ID END AS PRD_ID , CASE WHEN o.SHIPMENT_ID IS NOT NULL THEN prd.PRD_NAME END AS PRD_NAME FROM OUTBOUND o LEFT JOIN OUTBOUND_ITEM oi ON o.OUTBOUND_ID = oi.OUTBOUND_ID LEFT JOIN EMP e1 ON o.REQUEST_BY = e1.EMP_ID LEFT JOIN EMP e2 ON o.PROCESS_BY = e2.EMP_ID LEFT JOIN MATERIAL_MST mat ON oi.ITEM_ID = mat.MAT_ID LEFT JOIN PRODUCT_MST prd ON oi.ITEM_ID = prd.PRD_ID WHERE i.EXPECT_ARRIVAL_DATE >= #{startDate} AND i.EXPECT_ARRIVAL_DATE <= #{endDate}
AND ( mat.MAT_NAME LIKE '%' || #{keyword} || '%' OR prd.PRD_NAME LIKE '%' || #{keyword} || '%' OR oi.LOT_NO LIKE '%' || #{keyword} || '%' )
ORDER BY i.EXPECT_OUTBOUND_DATE DESC
